import React, {useState,useRef} from 'react';
// import ReactQuill from 'react-quill';
import Link from 'next/link';
import Head from 'next/head';
import LoadingOverlay from 'react-loading-overlay'

import AdminLayout from '../components/AdminLayout';

// import 'react-quill/dist/quill.snow.css'




const LOGIN_API = "https://6j0n7kx92c.execute-api.us-east-2.amazonaws.com/dev/api/login"

const Login = () => {
    const [username,setUsername] = useState("");
    const [password,setPassword] = useState("");
    const [isLoading,setIsLoading] = useState(false);
    const [error,setError] = useState(null);




    const handleChange=(value)=>{
      console.log("value ",value)
      setContent(value);
    }

    const login = () =>{
      if(username==""||password==""){
        setError("please complete the form and try again")
      }else{
        console.log("username and password ", username, password)
        setIsLoading(true)
        fetch(LOGIN_API, {
            method: 'POST',
            headers: {
                  'Accept': 'application/json',
                  'Content-Type': 'application/json'
            }, 
            body: JSON.stringify({
                 username: username,
                 password: password
               })
            })
            .then(response => response.json())
            .then(data =>{
              console.log(data);
              setIsLoading(false)

            })
            .catch(error =>{
              setIsLoading(false)
              console.log(error)
            }) 
      }
    }


    return(

        <LoadingOverlay
            active={isLoading}
            spinner
            text='Loading your content...'
            >


          <AdminLayout>
	          <div className="d-flex justify-content-center">
	          	<div className="auth-card">
		   			<div className="card card-small mb-4">
		                <div className="card-header border-bottom text-center">
		                    <h6 className="m-0">Login</h6>
		                </div>
		                <ul className="list-group list-group-flush">
		                    <li className="list-group-item px-3">
			                    <form>
			                        <strong className="text-muted d-block mb-2">Username</strong>
			                        <div className="input-group mb-3">
			                          <div className="input-group-prepend">
			                            <span className="input-group-text">@</span>
			                          </div>
			                          <input type="text" className="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1" /> 
			                        </div>

			                        <strong className="text-muted d-block mb-2">Password</strong>
			                        <div className="input-group mb-3">
			                          <div className="input-group input-group-seamless">
			                            <input type="password" className="form-control" id="form2-password" placeholder="Password" value="mySuperSecretPa$$word" />
			                            <span className="input-group-append">
			                              <span className="input-group-text">
			                                <i className="material-icons">lock</i>
			                              </span>
			                            </span>
			                          </div>
			                        </div>
			                        <div className="float-right">
			                        	<button type="button" class="mb-2 btn btn-sm btn-primary mr-1">Submit</button>
			                        </div>
			                    </form>
		                    </li>
		                </ul>
		            </div>
		        </div>
	        </div>
            <style jsx>{`
              .auth-card{
              	width: 40%;
              	margin-top: 5rem;
              }
            `}</style>
          </AdminLayout>
        </LoadingOverlay>
      )

}

export default Login;
