import Link from 'next/link';

import Layout from '../components/Layout';
import Banner from '../components/Banner';

import { FaRegComments } from 'react-icons/fa';





const Index = () => (
    <Layout>

      <div id="myCarousel" className="carousel slide" data-ride="carousel">
        <ol className="carousel-indicators">
          <li data-target="#myCarousel" data-slide-to="0" className="active"></li>
          <li data-target="#myCarousel" data-slide-to="1"></li>
          <li data-target="#myCarousel" data-slide-to="2"></li>
        </ol>
        <div className="carousel-inner">
          <div className="carousel-item banner-carousel-item  banner1 active">
            <div className="container">
              <div className="carousel-caption">
                <h3>Consultation</h3>
                <p>
                  All consultants provide advice and guidance on specic learning needs. 
                  You can discuss programmes and strategies that work to meet your childs 
                  targets as well as techniques to improve behaviour and life skills. We hope 
                  that by creating a clear pathway you are able to build condence for all 
                  involved in your childs developmental journey.
                </p>
                <p>
                  <Link href="/service">
                    <a className="btn btn-lg btn-secondary" role="button">Learn More</a>
                  </Link>
                </p>
              </div>
            </div>
          </div>
          <div className="carousel-item banner-carousel-item banner2">
