import {useState,useRef} from 'react';
import Link from 'next/link';
import {Editor, EditorState} from 'draft-js';

import Layout from '../components/Layout';
import Banner from '../components/Banner';



const CreatePost = () => {
    const [editorState, setEditorState] = useState(EditorState.createEmpty());

    const editor = useRef(null);

    const focusEditor =()=> {
      editor.current.focus();
    }

    return(
        <Layout>

          <div id="contact">
            <div className="content-wrap pb-0">
              <div className="container">
                <div className="row">
                  <div className="col-12 col-lg-6">

                    <form action="#" className="form-contact" id="contactForm" data-toggle="validator" novalidate="true">
                      <div className="form-group">
                        <input type="text" className="form-control" id="p_name" placeholder="Title" required="" />
                        <div className="help-block with-errors"></div>
                      </div>

                      <div className="form-group">
                        <p>editor</p>
                        <Editor editorState={editorState} onChange={()=>setEditorState} />
                      </div>

                      <div className="form-group">
                         <textarea id="p_message" className="form-control" rows="6" placeholder="Enter Your Message"></textarea>
                        <div className="help-block with-errors"></div>
                      </div>

                      <div class="form-group form-check">
                        <input type="checkbox" class="form-check-input" id="publish" />
                        <label class="form-check-label pl-1" for="exampleCheck1">publish</label>
                      </div>

                      <div className="form-group">
                        <div id="success"></div>
                        <button type="submit" className="btn btn-primary">Send Message</button>
                      </div>
                    </form>
                    <div className="spacer-content"></div>

                  </div>

                </div>
              </div>
            </div>
          </div>




          <style jsx>{`
            .card p {
              margin: 0;
              font-size: 1.25rem;
              line-height: 1.5;
            }



            @media (max-width: 600px) {
              .grid {
                width: 100%;
                flex-direction: column;
              }
            }
          `}</style>

          <style jsx global>{`
            html,
            body {
              padding: 0;
              margin: 0;
              font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen,
                Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, sans-serif;
            }


            .abc-logo{
              width: 3rem;
            }

            .body-text{
              max-width: 100% !important;
            }

            .services{
              padding-top: 0;
            }

            * {
              box-sizing: border-box;
            }
          `}</style>
        </Layout>
      )

}

export default CreatePost;
